    throw new Error("No se proporcionó ningún headers de autorización");
  }

  // Bearer eyJhbGciOiJIUzI1(...)
  const [type, token] = authorization.split(" ");

  if (type !== "Bearer") {
    throw new Error("El tipo de autorización debe ser Bearer");
  }

  return token;
};
